mapper.select:
select

from medicine t

  del_flag = 0

    and code LIKE '%' #{code} '%'


    and name LIKE '%' #{name} '%'


    and specification = #{specification}


    and manufacturer_name = #{manufacturer_name}


    and units = #{units}


    and price = #{price}


    and lot_number = #{lot_number}


    and validity_period = #{validity_period}




    ORDER BY ${page.orderBy}


    ORDER BY t.id DESC

mapper.update:
UPDATE medicine SET
del_flag = TRUE
WHERE id = #{id}
