mapper.select:
select

from purchase_moneytax t

    del_flag = 0

        and payment_category LIKE '%' #{payment_category} '%'


        and payment_mode LIKE '%' #{payment_mode} '%'


        and payment_money = #{payment_money}


        and work_flow = #{work_flow}


        and purchase_unit_price = #{purchase_unit_price}


        and purchase_money = #{purchase_money}


        and tax = #{tax}


        and tax_pay_mode = #{tax_pay_mode}


        and tax_pay_date = #{tax_pay_date}


        and invoice_number = #{invoice_number}


        and invoice_date = #{invoice_date}


        and create_date = #{create_date}


        and update_date = #{update_date}


        and del_flag = #{del_flag}




        ORDER BY ${page.orderBy}


        ORDER BY t.id DESC

mapper.update:
UPDATE purchase_moneytax SET
del_flag = TRUE
WHERE id = #{id}
